tasks.command:
echo "/* Social Icons */
.social-links {
  display: flex;
  gap: 1.5rem;
  margin-top: 2rem;
  flex-wrap: wrap;
}

.social-link {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #f5f5f7;
  color: var(--color-text-dark);
  transition: all 0.3s ease;
  text-decoration: none;
}

.social-link:hover {
  background: var(--color-accent);
  color: white;
  transform: translateY(-3px);
}

.social-link svg {
  width: 20px;
  height: 20px;
  fill: currentColor;
}" >> css/style.css
